.fade-slide-right {
    animation: fade-in 1.5s forwards, slide-right 1.5s;
}

.fade-in {
    animation: fade-in 3s forwards;
}

@keyframes fade-in {
    from {opacity: 0;}
    to {opacity: 1;}
}

@keyframes slide-right {
    from {transform: translateX(-5em);}
    to {transform: translateX(0);}
}


@keyframes float1 {
    from {transform: translate(0, 0) rotate(0.12turn);}
