    80% {transform: translate(-150px, -90px) rotate(-0.8turn);}
    to {transform: translate(-190px, -140px) rotate(-1turn);}
}

@keyframes float2 {
    from {transform: translate(0, 0) rotate(0.52turn);}
    20% {transform: translate(20px, 40px) rotate(0.72turn);}
    40% {transform: translate(25px, 80px) rotate(0.92turn);}
    60% {transform: translate(20px, 120px) rotate(1.12turn);}
    80% {transform: translate(15px, 160px) rotate(1.32turn);}
    to {transform: translate(5px, 200px) rotate(1.52turn);}
}

@keyframes float3 {
    from {transform: translate(0, 0) rotate(0.43turn);}
    20% {transform: translate(40px, -30px) rotate(0.2turn);}
    40% {transform: translate(80px, 0px) rotate(0.4turn);}
    60% {transform: translate(120px, 20px) rotate(0.6turn);}
    80% {transform: translate(160px, 40px) rotate(0.8turn);}
    to {transform: translate(200px, 60px) rotate(1turn);}
}

@keyframes float4 {
    from {transform: translate(0, 0) rotate(0.78turn);}
    20% {transform: translate(40px, -40px) rotate(0.63turn);}
    40% {transform: translate(20px, -80px) rotate(0.48turn);}
    60% {transform: translate(-20px, -120px) rotate(0.33turn);}
    80% {transform: translate(-60px, -160px) rotate(0.18turn);}
    to {transform: translate(-40px, -200px) rotate(0.03turn);}
}

@keyframes float5 {
    from {transform: translate(0, 0) rotate(0.67turn);}
    20% {transform: translate(40px, 20px) rotate(0.87turn);}
    40% {transform: translate(60px, 50px) rotate(1.17turn);}
    60% {transform: translate(80px, 70px) rotate(1.37turn);}
    80% {transform: translate(100px, 110px) rotate(1.57turn);}
    to {transform: translate(120px, 150px) rotate(1.77turn);}
}
/*swup transitions*/
.transition-fade {
    opacity: 1;
    transition: 1s;
}

html.is-animating .transition-fade {
    opacity: 0;
}

/*sizing*/
.fullscreen {
    height: 100vh;
    width: 100vw;
    position: absolute;
}

.golden-ratio {
    padding-top: 61.8%;
}

/*opacity*/
.overflow-hidden {
    overflow: hidden;
}

.hidden {
    opacity: 0;
    transition: 2s ease-out opacity;
}

.visible {
    opacity: 1;
}

.blur {
    filter: blur(3px);
}

/*page specific*/

/*-- coming-soon page*/
#coming-soon {
    height: 300px;
}

/*--home*/

#anim-1, #anim-2, #anim-3, #anim-4, #anim-5 {
    opacity: 0;
}

#section-1 {
    background-color: hsl(45, 100%, 98%);
    min-height: 85vh;
}

#content-1 {
    padding: 0;
    margin: 15vh 0;
}

#b-sig {
    min-width: 30%;
}



#section-2 {
    background-color:  hsl(45, 100%, 99%);
    margin: 0 4% 4em 0;
    min-height: 27em;
    position: relative;
}

#section-3 {
    text-align: center;
    padding-bottom: 50px;
}

.floating {
    font-size: 89px;
    opacity: 10%;
    position: absolute;
    user-select: none;
}

#l1 {
    bottom: -20%;
    left: 25%;
    transform: rotate(0.12turn);
    animation: float1 50s linear 1.5s both infinite alternate;
}

#l2 {
    bottom: 30%;
    left: 33%;
    transform: rotate(0.52turn);
    animation: float2 50s linear 1s both infinite alternate;
}

#l3 {
    bottom: 50%;
    right: 50%;
    transform: rotate(0.43turn);
    animation: float3 50s linear both infinite alternate;
}

#l4 {
    right: 40%;
    bottom: -10%;
    transform: rotate(0.78turn);
    animation: float4 40s linear both infinite alternate;
}

#l5 {
    bottom: 35%;
    right: 25%;
    transform: rotate(0.67turn);
    animation: float5 40s linear both infinite alternate;
}

@media screen and (max-width: 790px) {
    #b-sig {
        margin-bottom: 4em;
    }
    .double-column {
        column-count: 1;
    }
    h2 {
        line-height: normal;
    }
    ul  .open {
        backdrop-filter: blur(3px);
    }
}

@media screen and (max-width: 1050px) {
    #b-sig {
        margin: 0 10% 10%;
    }
    .single-column {
        max-width: 80%;
    }
}

